import React, { useState } from "react";
import { Container, Segment } from "semantic-ui-react";
import './Home.css'
import { useSpeechSynthesis } from 'react-speech-kit';

function Home() {
  const [text, setText] = useState('');
  const { speak } = useSpeechSynthesis();

  // Function to handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      const fileText = e.target.result;
      setText(fileText);
    };

    reader.readAsText(file);
  };

  const handleOnClick = () => {
    speak({ text: text });
  }

  return (
    <Container>
      <Segment>
        <h1>Text Story To Speech!</h1>
        {/* Add file input field for uploading text file */}
        <input
          type="file"
          accept=".txt"
          onChange={handleFileUpload}
        />
        <textarea className="textAreaStyle" value={text} onChange={(e) => { setText(e.target.value) }}></textarea>
        <button className="buttonStyle" onClick={handleOnClick}>Listen</button>
      </Segment>
    </Container>
  );
}

export default Home;
